var XWiki = (function(XWiki) {
  XWiki.RISCOSSPlatform = XWiki.RISCOSSPlatform || {};

  /* Setup the validation logic for a create entity form. */
  var initializeCreateEntityForm = function(form) {
    var showErrorMessage = function(element, message) {
      if(!element.classList.contains('xErrorField')) {
        element.addClassName('xErrorField');
        element.insert({'after' : '<div class="xErrorMsg">' + message + '</div>'});
      }
    };

    var removeErrorMessage = function(element) {
      if(element.classList.contains('xErrorField')) {
        element.removeClassName('xErrorField');
        var errorMessage = element.next('.xErrorMsg');
        errorMessage && errorMessage.remove();
      }
    };

    var entityId = form.down('.entityId');
    var entityName = form.down('.entityName');

    entityId.focus();

    form.observe('submit', function(event) {
      if(entityId.value.strip().length == 0) {
        showErrorMessage(entityId, '$services.localization.render("riscossPlatform.scopeManager.createEntity.entityIdMissingMessage")');
        event.stop();
      }
      else {
        removeErrorMessage(entityId);
      }

      if(entityName.value.strip().length == 0) {
        showErrorMessage(entityName, '$services.localization.render("riscossPlatform.scopeManager.createEntity.entityNameMissingMessage")');
        event.stop();
      }
      else {
        removeErrorMessage(entityName);
      }
    });
  };

  XWiki.RISCOSSPlatform.initializeCreateEntityDialog = function(dialog) {
    var form = dialog.content;
    initializeCreateEntityForm(form);

    var cancelButton = form.down('.secondary');
    cancelButton.observe('click', function(event) {
      event.stop();
      dialog.closeDialog();
    });
  }

  /* This is used to initialize the create scope forms that are present in the loaded page, if any. */
  var createEntityForms = $$('.createEntityForm');
  for(i = 0; i < createEntityForms.length; i++) {
    initializeCreateEntityForm(createEntityForms[i]);
  }

  return XWiki;
}(XWiki || {}));
